import React, { useState } from "react";
import "./SideBarModal.css";
import { useSelector, useDispatch } from "react-redux";
// import SideBarModal2 from "./SideBarModal2";
import * as actions from "../../../../Redux/actions/index.js";
import PostList from "./PostList";

export default function SideBarModal() {
  const [challengeTitle, setChallengeTitle] = useState("");
  const [challengeDescription, setChallengeDescription] = useState("");
  const [challengeCapacity, setChallengeCapacity] = useState();
  const [challengeFrequency, setChallengeFrequency] = useState();

  const [challengeMessage, setChallengeMessage] = useState(null);

  const showModal1 = useSelector((state) => state.modalOnOff.showModal1);
  const dispatch = useDispatch();

  function handleSubmit(e) {
    e.preventDefault();
    console.log();
  }

  function handleChange(e) {
    e.preventDefault();
    setChallengeCapacity();
    console.log("challengeCapacity", challengeCapacity);
  }

  return (
    <>
      <div>
        <div
          className={showModal1 ? "Background" : null}
          onClick={() => {
            dispatch(actions.showModal(false));
          }}
        ></div>

        <div className={showModal1 ? "ModalContainer" : "hidden"}>
          <div>새로운 챌린지 생성하기</div>
        </div>
        <div>
          <form onSubmit={handleSubmit}>
            <label>
              챌린지 참여 최대 인원:
              <select>
                <option value="2">2</option>
                <option value="4">4</option>
                <option value="6">6</option>
                <option value="8">8</option>
              </select>
            </label>
          </form>
          <form>
            <label>
              챌린지 참여 빈도:
              <select>
                <option value="1">매일 참여</option>
                <option value="2">주 6회 참여</option>
                <option value="3">주 5회 참여</option>
                <option value="4">주 4회 참여</option>
                <option value="5">주 3회 참여</option>
                <option value="6">주 2회 참여</option>
                <option value="7">주 1회 참여</option>
              </select>
              <button type="submit" onClick={handleSubmit}>
                챌린지 생성
              </button>
            </label>
          </form>
        </div>
      </div>
    </>
  );
}
