import React, { useState } from 'react';
import { Button, Container, Nav, Navbar } from 'react-bootstrap';
import styles from './index.module.css';
import SignUpModal from '../../../modals/SignUpModal';
import SignInModal from '../../../modals/SignInModal';

import classNames from "classnames/bind";
const cx = classNames.bind(styles);

export default function Header(props) {
  const { rooms, roomId, setRoomId } = props;
  const [signUpModalOn, setSignUpModalOn] = useState(false);
  const [signInModalOn, setSignInModalOn] = useState(false);

  return (
    <>
      <SignUpModal
        show={signUpModalOn}
        onHide={() => setSignUpModalOn(false)}
      />
      <SignInModal
        show={signInModalOn}
        onHide={() => setSignInModalOn(false)}
      />
      <div className={styles.body}>
        <Navbar bg="light" expand="lg">
          <Container>
            {/* <Navbar.Brand>목표달성! 외계생물 기르기</Navbar.Brand> */}
            <Navbar.Toggle aria-controls="basic-navbar-nav" />
            <Navbar.Collapse id="basic-navbar-nav">
              <Nav className="ml-auto">
                <div className={cx("item", "itemTitle")}>
                  <h1 className={styles.title}>{`Aquarium: ROOM ${roomId}`}</h1>
                </div>
                <div className={cx("item", "itemRoom")}>
                  {rooms.map((roomId) => (
                    <button
                      key={roomId}
                      onClick={() => setRoomId(roomId)}
                    >{`Room ${roomId}`}</button>
                  ))}
                </div>
                <div className={cx("item", "itemUser")}>
                  <Nav.Link>
                    <Button
                      variant="secondary"
                      onClick={() => setSignUpModalOn(true)}
                    >
                      회원가입
                    </Button>
                    <Button
                      variant="primary"
                      onClick={() => setSignInModalOn(true)}
                    >
                      로그인
                    </Button>
                  </Nav.Link>
                </div>
              </Nav>
            </Navbar.Collapse>
          </Container>
        </Navbar>
      </div>
    </>
  );
}
