import { handleActions } from "redux-actions";
import * as types from "../actions/ActionTypes";

const initialState = {
  roomId: null,
  roomTitle: null,
  aliens: [],
  selectedAlien: null,
  challenge: {},
  messages: [],
};

const room = handleActions(
  {
    // 화면에 나타나는 alien들을 페이지별로 다르게 받아옴
    [types.CURRENT_ROOM]: (
      state,
      { payload: { roomId, aliens, roomTitle, challenge } }
    ) => ({
      ...state,
      roomId: roomId,
      roomTitle: roomTitle || null,
      aliens: aliens || [],
      selectedAlien: null,
      challenge: challenge || {},
      messages: [],
    }),

    [types.SELECT_ALIEN]: (state, { payload: selectedAlien }) => ({
      ...state,
      selectedAlien: selectedAlien,
    }),

    [types.MESSAGE_UPDATE]: (state, { payload: msg }) => ({
      ...state,
      messages: [...state.messages, msg],
    }),
  },
  initialState
);
export default room;
