import React, { useState } from "react";
import { Button, Container, Form, Modal } from "react-bootstrap";
import HorizonLine from "../components/HorizonLine.js";
import * as api from "../apis/index.js";

const SignUpModal = ({ show, onHide, setSignUpModalOn, setLoginStatus }) => {
  // const [showSignUp, setShowSignUp] = useState(true);

  const [userName, setUserName] = useState("");
  const [userEmail, setUserEmail] = useState("");
  const [userPassword, setUserPassword] = useState("");
  const [userConfirm, setUserConfirm] = useState("");

  let signUpData = { userName, userEmail, userPassword, userConfirm };
  console.log("userData", userData);

  const postSignIn = async () => {
    const response = await api.post("/login_process", userData);
    console.log("response", response);
    const data = await response.json();
    console.log("data", data);
  };

  useEffect(() => {
    try {
      postSignIn();
    } catch (err) {
      console.log("ERROR", err);
    }
  }, []);

  return (
    <Modal
      show={show}
      onHide={onHide}
      aria-labelledby="contained-modal-title-vcenter"
      centered
    >
      <Container>
        <Modal.Header closeButton>
          <Modal.Title id="contained-modal-title-vcenter">회원가입</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form>
            <Form.Group>
              <Form.Label>이름</Form.Label>
              <Form.Control
                type="text"
                placeholder="이재열"
                onChange={(e) => {
                  setUserName(e.target.value);
                }}
              />
            </Form.Group>

            <Form.Group>
              <Form.Label>이메일</Form.Label>
              <Form.Control
                type="email"
                placeholder="helloalien@jungle.com"
                onChange={(e) => {
                  setUserEmail(e.target.value);
                }}
              />
            </Form.Group>

            <Form.Group>
              <Form.Label>패스워드</Form.Label>
              <Form.Control
                type="password"
                placeholder="********"
                onChange={(e) => {
                  setUserPassword(e.target.value);
                }}
              />
            </Form.Group>

            <Form.Group>
              <Form.Label>패스워드 확인</Form.Label>
              <Form.Control
                type="password"
                placeholder="********"
                onChange={(e) => {
                  setUserConfirm(e.target.value);
                }}
              />
            </Form.Group>
            <Button
              className="my-3"
              type="button"
              variant="success"
              style={{
                width: "100%",
              }}
              // onClick={() => setSignUpModalOn(false)}
              onClick={() => postSignUp()}
            >
              회원가입
            </Button>
          </Form>
        </Modal.Body>
      </Container>
    </Modal>
  );
};

export default SignUpModal;
