import React, { useState, useEffect } from "react";
import { Button, Container, Form, Modal } from "react-bootstrap";
import styles from "./SignInModal.module.css";
import api from "../apis/index.js";

const SignInModal = ({ show, onHide, setSignInModalOn, setLoginStatus }) => {
  const [userEmail, setUserEmail] = useState("");
  const [userPassword, setUserPassword] = useState("");

  const [signInClicked, setSignInClicked] = useState(false);
  const [signInError, setSignInError] = useState(null);
  const [signInMessage, setSignInMessage] = useState(null);

  const [signInStatus, setSignInStatus] = useState(false);
  const [signInResult, setSignInResult] = useState(null);

  function validateSignIn(userEmail, userPassword) {
    if (
      (userEmail !== "") &
      !/^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{2,3})+$/.test(userEmail)
    ) {
      setSignInMessage("입력하신 이메일 주소가 유효하지 않습니다.");
      return false;
    }

    // if (userPassword !== "") {
    //   setSignInMessage("입력하신 이메일과 패스워드가 일치하지 않습니다.");
    //   return false;
    // }

    if (userEmail === "" || userPassword === "") {
      setSignInMessage("입력하지 않은 회원정보가 있습니다.");
      return false;
    }

    setSignInMessage(null);
    return true;
  }

  const postSignIn = async () => {
    let signInData = { email: userEmail, pwd: userPassword };
    const response = await api.post("/user/login", signInData);
    console.log("response", response);
    const ddd = setSignInResult(response.data.result);
    console.log("signInResult111", signInResult);
    const fff = await (() => {
      if (signInResult === "success") {
        {
          console.log("signInStatus222", signInStatus);
          signInSuccess();
          console.log("signInStatus333", signInStatus);
        }
      } else {
        {
          console.log("signInFailure444", signInStatus);
          signInFailure();
          console.log("signInFailure555", signInStatus);
        }
      }
    });

    // if (response.data.result === "success") {
    //   {
    //     signInSuccess();
    //     console.log("signInSuccess", signInSuccess());
    //   }
    // } else {
    //   {
    //     signInFailure();
    //     console.log("signInFailure", signInFailure());
    //   }
    // }
  };

  useEffect(() => {
    try {
      setSignInError(null);
      postSignIn();
    } catch (err) {
      setSignInError(err);
      console.log("SIGN-IN ERROR", signInError);
    }
  }, [signInClicked]);

  function signInSuccess() {
    return () => {
      setSignInClicked(false);
      setSignInStatus(true);
      setLoginStatus(true);
      console.log("signInStatus9999", signInStatus);
      setSignInModalOn(false);
    };
  }

  function signInFailure() {
    return () => {
      setSignInClicked(false);
    };
  }

  useEffect(() => {
    if (signInResult === "success") {
      {
        signInSuccess();
        console.log("signInStatus8888", signInStatus);
      }
    } else {
      {
        signInFailure();
        console.log("signInFailure", signInFailure());
      }
    }
  }, [signInResult]);

  const onClick = (event) => {
    event.preventDefault();
    setSignInMessage(null);
    if (validateSignIn(userEmail, userPassword) === true) {
      setSignInClicked(true);
    }
  };

  return (
    <Modal
      className={styles.modal__form}
      show={show}
      onHide={onHide}
      aria-labelledby="contained-modal-title-vcenter"
      centered
    >
      <Container className={styles.container}>
        <Modal.Header
          closeButton
          onClick={() => {
            setUserEmail("");
            setUserPassword("");
            setSignInMessage(null);
          }}
        >
          <Modal.Title id="contained-modal-title-vcenter">로그인</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form className={styles.form__signin}>
            <Form.Group>
              <Form.Label>이메일</Form.Label>
              <Form.Control
                className={styles.form__input}
                type="email"
                placeholder="helloalien@jungle.com"
                onChange={(e) => {
                  setUserEmail(e.target.value);
                }}
              />
              <br />
            </Form.Group>

            <Form.Group>
              <Form.Label>패스워드</Form.Label>
              <Form.Control
                type="password"
                placeholder="********"
                onChange={(e) => {
                  setUserPassword(e.target.value);
                }}
              />
              <br />
            </Form.Group>

            <Form.Group className={styles.form__signin__message}>
              {signInMessage}
              <br />
            </Form.Group>

            <Button
              className="my-3"
              type="button"
              variant="success"
              style={{
                width: "100%",
              }}
              onClick={onClick}
            >
              로그인
            </Button>
          </Form>
        </Modal.Body>
      </Container>
    </Modal>
  );
};

export default SignInModal;
