import React, { useState, useEffect } from "react";
import { Button, Container, Form, Modal } from "react-bootstrap";
import HorizonLine from "../components/HorizonLine.js";
import * as api from "../apis/index.js";

const SignUpModal = ({ show, onHide, setSignUpModalOn, setLoginStatus }) => {
  // const [showSignUp, setShowSignUp] = useState(true);
  const [userNickname, setUserNickname] = useState("");
  const [userEmail, setUserEmail] = useState("");
  const [userPassword, setUserPassword] = useState("");
  const [userConfirm, setUserConfirm] = useState("");
  // 에러 변수 추가
  const [signUpClicked, setSignUpClicked] = useState(false);

  let signUpData = { userNickname, userEmail, userPassword, userConfirm };
  console.log("signUpData", signUpData);

  const postSignUp = async () => {
    const response = await api.post("/login_process", signUpData);
    console.log("response", response);
    const data = await response.json();
    console.log("data", data);
  };

  console.log(signUpClicked);

  useEffect(() => {
    try {
      postSignUp();
    } catch (err) {
      console.log("ERROR", err);
    }
  }, [signUpClicked]);

  // 회원가입 버튼을 처음 눌렀을 때만 포스트 요청이 보내지도록 변경

  function signUpSuccess() {
    return () => {
      setSignUpModalOn(false);
    };
  }

  function signUpFailure() {
    return () => {
      // 회원가입 실패 alert 창 또는 에러 메세지 띄우기
      // "이미 존재하는 닉네임입니다."
      // 이미 존재하는 이메일인 경우 에러 메세지 "이미 존재하는 이메일입니다."
      // 패스워드가 다른 경우 "입력하신 패스워드가 일치하지 않습니다."
    };
  }

  return (
    <Modal
      show={show}
      onHide={onHide}
      aria-labelledby="contained-modal-title-vcenter"
      centered
    >
      <Container>
        <Modal.Header closeButton>
          <Modal.Title id="contained-modal-title-vcenter">회원가입</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form>
            <Form.Group>
              <Form.Label>닉네임</Form.Label>
              <Form.Control
                type="text"
                placeholder="이재열"
                onChange={(e) => {
                  setUserNickname(e.target.value);
                }}
              />
            </Form.Group>

            <Form.Group>
              <Form.Label>이메일</Form.Label>
              <Form.Control
                type="email"
                placeholder="helloalien@jungle.com"
                onChange={(e) => {
                  setUserEmail(e.target.value);
                }}
              />
            </Form.Group>

            <Form.Group>
              <Form.Label>패스워드</Form.Label>
              <Form.Control
                type="password"
                placeholder="********"
                onChange={(e) => {
                  setUserPassword(e.target.value);
                }}
              />
            </Form.Group>

            <Form.Group>
              <Form.Label>패스워드 확인</Form.Label>
              <Form.Control
                type="password"
                placeholder="********"
                onChange={(e) => {
                  setUserConfirm(e.target.value);
                }}
              />
            </Form.Group>
            <Button
              className="my-3"
              type="button"
              variant="success"
              style={{
                width: "100%",
              }}
              onClick={() => {
                setSignUpClicked(true);
                postSignUp();
              }}
            >
              회원가입
            </Button>
          </Form>
        </Modal.Body>
      </Container>
    </Modal>
  );
};

export default SignUpModal;
