import React, { useState } from "react";
import "./SignInModal.css";
import { LockClosedIcon } from "@heroicons/react/solid";
// import { Button, Container, Form, Modal } from "react-bootstrap";
// import styles from "./SignInModal.module.css";
import api from "../apis/index.js";
import { useDispatch } from "react-redux";
import * as actions from "../Redux/actions";

// const SignInModal = ({ show, onHide, setSignInModalOn, setLoginStatus }) => {
const SignInModal = ({ show, onHide, setSignInModalOn }) => {
  const dispatch = useDispatch();

  const [userEmail, setUserEmail] = useState("");
  const [userPassword, setUserPassword] = useState("");

  // const [signInClicked, setSignInClicked] = useState(false);
  const [signInMessage, setSignInMessage] = useState(null);

  function validateSignIn(userEmail, userPassword) {
    if (
      (userEmail !== "") &
      !/^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{2,3})+$/.test(userEmail)
    ) {
      setSignInMessage("이메일 주소가 유효하지 않습니다.");
      return false;
    }

    if (userEmail === "" || userPassword === "") {
      setSignInMessage("입력하지 않은 회원정보가 있습니다.");
      return false;
    }

    setSignInMessage(null);
    return true;
  }

  const handleSubmit = (e) => {
    e.preventDefault();
    // if (signInClicked) return;
    if (!validateSignIn(userEmail, userPassword)) return;
    setSignInMessage(null);
    // setSignInClicked(true);
    postSignIn();
  };

  const postSignIn = async () => {
    let signInData = { email: userEmail, pwd: userPassword };
    // 1단계: 로그인 요청
    let res = await api.post("/user/login", signInData);
    console.log("res", res);
    if (res.data.result !== "success") {
      setSignInMessage("이메일과 패스워드가 일치하지 않습니다.");
      return;
      // setSignInClicked(false);
    }
    let user = res.data;
    delete user.result;
    user.login = true;
    user.challenges = [];
    // 2단계: 유저 관련 정보 확인 (참여중 챌린지 등)
    res = await api.get("/user/personalinfo");
    if (res.data.result === "success") {
      user.challenges = res.data.Challenge;
    }
    // 리덕스에 저장
    dispatch(actions.checkUser(user));
    setSignInModalOn(false);
  };

  return (
    <div
      id="SignInModal"
      class="flex flex-col w-full max-w-md px-4 py-8 bg-white rounded-lg shadow dark:bg-gray-800 sm:px-6 md:px-8 lg:px-10"
      show={show}
      onHide={onHide}
    >
      <div class="self-center mb-6 text-xl font-light text-gray-600 sm:text-2xl dark:text-white">
        나만의 계정으로 로그인
      </div>
      <div class="mt-8">
        <form action="#" autoComplete="off">
          <div class="flex flex-col mb-2">
            <div class="flex relative ">
              <span class="rounded-l-md inline-flex  items-center px-3 border-t bg-white border-l border-b  border-gray-300 text-gray-500 shadow-sm text-sm">
                <svg
                  width="15"
                  height="15"
                  fill="currentColor"
                  viewBox="0 0 1792 1792"
                  xmlns="http://www.w3.org/2000/svg"
                >
                  <path d="M1792 710v794q0 66-47 113t-113 47h-1472q-66 0-113-47t-47-113v-794q44 49 101 87 362 246 497 345 57 42 92.5 65.5t94.5 48 110 24.5h2q51 0 110-24.5t94.5-48 92.5-65.5q170-123 498-345 57-39 100-87zm0-294q0 79-49 151t-122 123q-376 261-468 325-10 7-42.5 30.5t-54 38-52 32.5-57.5 27-50 9h-2q-23 0-50-9t-57.5-27-52-32.5-54-38-42.5-30.5q-91-64-262-182.5t-205-142.5q-62-42-117-115.5t-55-136.5q0-78 41.5-130t118.5-52h1472q65 0 112.5 47t47.5 113z"></path>
                </svg>
              </span>
              <input
                type="text"
                id="sign-in-email"
                class=" rounded-r-lg flex-1 appearance-none border border-gray-300 w-full py-2 px-4 bg-white text-gray-700 placeholder-gray-400 shadow-sm text-base focus:outline-none focus:ring-2 focus:ring-purple-600 focus:border-transparent"
                placeholder="santoryu1118@gmail.com"
                onChange={(e) => {
                  setUserEmail(e.target.value);
                }}
              />
            </div>
          </div>
          <div class="flex flex-col mb-6">
            <div class="flex relative ">
              <span class="rounded-l-md inline-flex  items-center px-3 border-t bg-white border-l border-b  border-gray-300 text-gray-500 shadow-sm text-sm">
                <svg
                  width="15"
                  height="15"
                  fill="currentColor"
                  viewBox="0 0 1792 1792"
                  xmlns="http://www.w3.org/2000/svg"
                >
                  <path d="M1376 768q40 0 68 28t28 68v576q0 40-28 68t-68 28h-960q-40 0-68-28t-28-68v-576q0-40 28-68t68-28h32v-320q0-185 131.5-316.5t316.5-131.5 316.5 131.5 131.5 316.5q0 26-19 45t-45 19h-64q-26 0-45-19t-19-45q0-106-75-181t-181-75-181 75-75 181v320h736z"></path>
                </svg>
              </span>
              <input
                type="password"
                id="sign-in-email"
                class=" rounded-r-lg flex-1 appearance-none border border-gray-300 w-full py-2 px-4 bg-white text-gray-700 placeholder-gray-400 shadow-sm text-base focus:outline-none focus:ring-2 focus:ring-purple-600 focus:border-transparent"
                placeholder="********"
                onChange={(e) => {
                  setUserPassword(e.target.value);
                }}
              />
            </div>
          </div>
          <div class="flex items-center mb-6 -mt-4">
            <div class="flex ml-auto">
              <a
                href="#"
                class="inline-flex text-xs font-thin text-gray-500 sm:text-sm dark:text-gray-100 hover:text-gray-700 dark:hover:text-white"
              >
                패스워드 찾기
              </a>
            </div>
          </div>
          <div class="flex w-full">
            <button
              type="submit"
              class="py-2 px-4  bg-purple-600 hover:bg-purple-700 focus:ring-purple-500 focus:ring-offset-purple-200 text-white w-full transition ease-in duration-200 text-center text-base font-semibold shadow-md focus:outline-none focus:ring-2 focus:ring-offset-2  rounded-lg"
              onClick={handleSubmit}
            >
              로그인
            </button>
          </div>
        </form>
      </div>
      <div class="flex items-center justify-center mt-6">
        <a
          href="#"
          target="_blank"
          class="inline-flex items-center text-xs font-thin text-center text-gray-500 hover:text-gray-700 dark:text-gray-100 dark:hover:text-white"
        >
          <span class="ml-2">회원가입</span>
        </a>
      </div>
    </div>
  );
};

export default SignInModal;

