import React, { useState } from "react";
import "./SignInModal.css";
import { LockClosedIcon } from "@heroicons/react/solid";
// import { Button, Container, Form, Modal } from "react-bootstrap";
// import styles from "./SignInModal.module.css";
import api from "../apis/index.js";
import { useDispatch } from "react-redux";
import * as actions from "../Redux/actions";

// const SignInModal = ({ show, onHide, setSignInModalOn, setLoginStatus }) => {
const SignInModal = ({ show, onHide, setSignInModalOn }) => {
  const dispatch = useDispatch();

  const [userEmail, setUserEmail] = useState("");
  const [userPassword, setUserPassword] = useState("");

  // const [signInClicked, setSignInClicked] = useState(false);
  const [signInMessage, setSignInMessage] = useState(null);

  function validateSignIn(userEmail, userPassword) {
    if (
      (userEmail !== "") &
      !/^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{2,3})+$/.test(userEmail)
    ) {
      setSignInMessage("이메일 주소가 유효하지 않습니다.");
      return false;
    }

    if (userEmail === "" || userPassword === "") {
      setSignInMessage("입력하지 않은 회원정보가 있습니다.");
      return false;
    }

    setSignInMessage(null);
    return true;
  }

  const handleSubmit = (e) => {
    e.preventDefault();
    // if (signInClicked) return;
    if (!validateSignIn(userEmail, userPassword)) return;
    setSignInMessage(null);
    // setSignInClicked(true);
    postSignIn();
  };

  const postSignIn = async () => {
    let signInData = { email: userEmail, pwd: userPassword };
    // 1단계: 로그인 요청
    let res = await api.post("/user/login", signInData);
    console.log("res", res);
    if (res.data.result !== "success") {
      setSignInMessage("이메일과 패스워드가 일치하지 않습니다.");
      return;
      // setSignInClicked(false);
    }
    let user = res.data;
    delete user.result;
    user.login = true;
    user.challenges = [];
    // 2단계: 유저 관련 정보 확인 (참여중 챌린지 등)
    res = await api.get("/user/personalinfo");
    if (res.data.result === "success") {
      user.challenges = res.data.Challenge;
    }
    // 리덕스에 저장
    dispatch(actions.checkUser(user));
    setSignInModalOn(false);
  };

  return (
    <>
      <div className="min-h-full flex items-center justify-center py-12 px-4 sm:px-6 lg:px-8">
        <div className="max-w-md w-full space-y-8">
          <div>
            <img
              className="mx-auto h-12 w-auto"
              src="https://tailwindui.com/img/logos/workflow-mark-indigo-600.svg"
              alt="Workflow"
            />
            <h2 className="mt-6 text-center text-3xl font-extrabold text-gray-900">
              로그인 고고
            </h2>
            <p className="mt-2 text-center text-sm text-gray-600">
              아직 회원이 아니면{" "}
              <a
                href="#"
                className="font-medium text-indigo-600 hover:text-indigo-500"
              >
                회원가입 고고
              </a>
            </p>
          </div>
          <form className="mt-8 space-y-6" action="#" method="POST">
            <input type="hidden" name="remember" defaultValue="true" />
            <div className="rounded-md shadow-sm -space-y-px">
              <div>
                <label htmlFor="email-address" className="sr-only">
                  이메일
                </label>
                <input
                  id="email-address"
                  name="email"
                  type="email"
                  autoComplete="email"
                  required
                  className="appearance-none rounded-none relative block w-full px-3 py-2 border border-gray-300 placeholder-gray-500 text-gray-900 rounded-t-md focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 focus:z-10 sm:text-sm"
                  placeholder="Email address"
                />
              </div>
              <div>
                <label htmlFor="password" className="sr-only">
                  패스워드
                </label>
                <input
                  id="password"
                  name="password"
                  type="password"
                  autoComplete="current-password"
                  required
                  className="appearance-none rounded-none relative block w-full px-3 py-2 border border-gray-300 placeholder-gray-500 text-gray-900 rounded-b-md focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 focus:z-10 sm:text-sm"
                  placeholder="Password"
                />
              </div>
            </div>

            <div className="flex items-center justify-between">
              <div className="flex items-center">
                <input
                  id="remember-me"
                  name="remember-me"
                  type="checkbox"
                  className="h-4 w-4 text-indigo-600 focus:ring-indigo-500 border-gray-300 rounded"
                />
                <label
                  htmlFor="remember-me"
                  className="ml-2 block text-sm text-gray-900"
                >
                  로그인 상태 유지
                </label>
              </div>

              <div className="text-sm">
                <a
                  href="#"
                  className="font-medium text-indigo-600 hover:text-indigo-500"
                >
                  이메일/패스워드 찾기
                </a>
              </div>
            </div>

            <div>
              <button
                type="submit"
                className="group relative w-full flex justify-center py-2 px-4 border border-transparent text-sm font-medium rounded-md text-white bg-indigo-600 hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500"
              >
                <span className="absolute left-0 inset-y-0 flex items-center pl-3">
                  <LockClosedIcon
                    className="h-5 w-5 text-indigo-500 group-hover:text-indigo-400"
                    aria-hidden="true"
                  />
                </span>
                로그인
              </button>
            </div>
          </form>
        </div>
      </div>
    </>
  );
};

export default SignInModal;

